var el,hd,screenname;el=document.getElementById("TM_FTR");el&&el.parentNode.removeChild(el);try{if(document.querySelector(".paging.pageDisplay").innerHTML!=="1"){document.body.classList.add("subsequent-page-of-thread");document.querySelector(".replies").scrollIntoView()}screenname=document.querySelector(".firstPostBox .username a").innerHTML;Array.prototype.slice.call(document.querySelectorAll(".username a")).forEach(function(a){a.innerHTML===screenname&&a.classList.add("original-poster-username")})}catch(e){}el=document.createElement("link");hd=document.getElementsByTagName("head")[0];el.type="text/css";el.rel="stylesheet";el.href="http://patik.com/code/user-scripts/readable-trip-advisor.css";el.media="screen";hd.appendChild(el);